var create_plugin = (function() {
	var m_plugin_host = null;
	var m_is_init = false;
	var m_forward_button = null;
	var m_laser_button = null;

	var SYSTEM_DOMAIN = UPSTREAM_DOMAIN + UPSTREAM_DOMAIN;
	var OMNI_LASER_DOMAIN = UPSTREAM_DOMAIN + "omni_laser.";
	var OMNI_WHEEL_DOMAIN = UPSTREAM_DOMAIN + "omni_wheel.";
	var MOVE_TIMEOUT = 60 * 1000;// 1min

	var FORWARD_ICON = "data:image/png;base64,";
	var FORWARD_PUSHED_ICON = "data:image/png;base64,";
	var LASER_ICON = "data:image/png;base64,";
	var LASER_PUSHED_ICON = "data:image/png;base64,";

	function create_forward_button(plugin) {
		var button = document.createElement("img");
		button.src = FORWARD_ICON;
		button.down = false;
		var mousedownFunc = function(ev) {
			plugin.event_handler_act("MOVE");
			
			button.down = true;

			button.src = FORWARD_PUSHED_ICON;
		}
		button.mouseupFunc = function() {
			plugin.event_handler_act("STOP");
			
			button.down = false;

			button.src = FORWARD_ICON;
		}
		var preventFunc = function(ev) {
			ev.preventDefault();
			ev.stopPropagation();
		}
		button.addEventListener("touchstart", mousedownFunc);
		button.addEventListener("mousedown", mousedownFunc);
		button.addEventListener("dragstart", preventFunc);

		return button;
	}
	function create_laser_button(plugin) {
		var button = document.createElement("img");
		button.src = LASER_ICON;
		button.down = false;
		
		var sx = 0, sy = 0;
		var mousedownFunc = function(ev) {
			plugin.event_handler_act("FIRE");

			if (ev.type == "touchstart") {
				ev.clientX = ev.pageX;
				ev.clientY = ev.pageY;
			}
			button.down = true;
			sx = ev.clientX;
			sy = ev.clientY;

			button.src = LASER_PUSHED_ICON;
		}
		button.mouseupFunc = function() {
			plugin.event_handler_act("FIRE_OFF");

			button.down = false;

			button.src = LASER_ICON;
		}
		button.mousemoveFunc = function(ev) {
			if (ev.type == "touchmove") {
				ev.clientX = ev.pageX;
				ev.clientY = ev.pageY;
				ev.button = 0;
			}
			if (!button.down || ev.button != 0) {
				return;
			}
			var dx = -(ev.clientX - sx);
			var dy = -(ev.clientY - sy);

			var threshold = 5;
			var roll_diff = parseInt(dx / threshold) * threshold;
			var pitch_diff = parseInt(dy / threshold) * threshold;
			sx -= roll_diff;
			sy -= pitch_diff;

			if (roll_diff == 0) {
				// do nothing
			} else {
				plugin.event_handler_act("INCREMENT_YAW " + roll_diff);
			}
			if (pitch_diff == 0) {
				// do nothing
			} else {
				plugin.event_handler_act("INCREMENT_PITCH " + pitch_diff);
			}
			ev.preventDefault();
			ev.stopPropagation();
		}
		var preventFunc = function(ev) {
			ev.preventDefault();
			ev.stopPropagation();
		}
		button.addEventListener("touchstart", mousedownFunc);
		button.addEventListener("mousedown", mousedownFunc);
		button.addEventListener("dragstart", preventFunc);

		return button;
	}
	function init(plugin) {
		m_foward_button = create_forward_button(plugin);
		m_foward_button.style.position = 'absolute';
		m_foward_button.width = 50;
		m_foward_button.height = 50;
		m_foward_button
			.setAttribute("style", "position:absolute; bottom:33%; right:10%;");

		m_laser_button = create_laser_button(plugin);
		m_laser_button.style.position = 'absolute';
		m_laser_button.width = 50;
		m_laser_button.height = 50;
		m_laser_button
			.setAttribute("style", "position:absolute; bottom:66%; right:10%;");

		var mouseupFunc = function(ev) {
			if(m_foward_button.down){
				m_foward_button.mouseupFunc(ev);
			}
			if(m_laser_button.down){
				m_laser_button.mouseupFunc(ev);
			}
		}
		var mousemoveFunc = function(ev) {
			if(m_laser_button.down){
				m_laser_button.mousemoveFunc(ev);
			}
		}
		document.addEventListener("touchend", mouseupFunc);
		document.addEventListener("mouseup", mouseupFunc);
		document.addEventListener("touchmove", mousemoveFunc, true);
		document.addEventListener("mousemove", mousemoveFunc, true);
		
		document.body.appendChild(m_foward_button);
		document.body.appendChild(m_laser_button);
	}
	return function(plugin_host) {
		m_plugin_host = plugin_host;
		var step = 5;
		var interval = 50;
		var timer;
		var timer_key;
		var menu_visible = false;
		var stereo_enabled = false;
		var plugin = {
			init_options : function(options) {
				if (!m_is_init) {
					m_is_init = true;
					init(plugin);
				}
			},
			event_handler : function(sender, event) {
				if (sender == "PLUGIN_HOST") {
					switch (event) {
						case "STEREO_ENABLED" :
							m_foward_button.style.display = "none";
							m_laser_button.style.display = "none";
							break;
						case "STEREO_DISABLED" :
							m_foward_button.style.display = "block";
							m_laser_button.style.display = "block";
							break;
					}
					return;
				} else if (sender == "ICADE") {
					switch (event) {
						case "H_BUTTON_DOWN" :
							event = "FIRE";
							break;
						case "H_BUTTON_UP" :
							event = "FIRE_OFF";
							break;
						case "B_BUTTON_UP" :
							event = "STEREO_ENABLED";
							break;
						case "G_BUTTON_DOWN" :
							event = "MOVE";
							break;
						case "G_BUTTON_UP" :
							event = "STOP"
							break;
						case "A_BUTTON_UP" :
							event = "MENU_VISIBLE";
							break;
						case "LEFT_BUTTON_DOWN" :
						case "RIGHT_BUTTON_DOWN" :
						case "UP_BUTTON_DOWN" :
						case "DOWN_BUTTON_DOWN" :
							if (menu_visible) {
							} else {
								clearInterval(timer);
								timer_key = event;
								timer = setInterval(function() {
									switch (event) {
										case "LEFT_BUTTON_DOWN" :
											plugin
												.event_handler_act("INCREMENT_YAW");
											break;
										case "RIGHT_BUTTON_DOWN" :
											plugin
												.event_handler_act("DECREMENT_YAW");
											break;
										case "UP_BUTTON_DOWN" :
											plugin
												.event_handler_act("INCREMENT_PITCH");
											break;
										case "DOWN_BUTTON_DOWN" :
											plugin
												.event_handler_act("DECREMENT_PITCH");
											break;
									}
								}, interval);
							}
							return;
						case "RIGHT_BUTTON_UP" :
							if (menu_visible) {
								event = "SELECT_ACTIVE_MENU";
							} else if (timer_key == "RIGHT_BUTTON_DOWN") {
								clearInterval(timer);
							}
							break;
						case "LEFT_BUTTON_UP" :
							if (menu_visible) {
								event = "DESELECT_ACTIVE_MENU";
							} else if (timer_key == "LEFT_BUTTON_DOWN") {
								clearInterval(timer);
							}
							break;
						case "UP_BUTTON_UP" :
							if (menu_visible) {
								event = "BACK2PREVIOUSE_MENU";
							} else if (timer_key == "UP_BUTTON_DOWN") {
								clearInterval(timer);
							}
							break;
						case "DOWN_BUTTON_UP" :
							if (menu_visible) {
								event = "GO2NEXT_MENU";
							} else if (timer_key == "DOWN_BUTTON_DOWN") {
								clearInterval(timer);
							}
							break;
					}
				} else if (sender == "GAMEPAD") {
					console.log(event);
					switch (event) {
						case "0_BUTTON_UP" :
							event = "PID_ENABLED";
							break;
						case "1_BUTTON_UP" :
							event = "STEREO_ENABLED";
							break;
						case "4_BUTTON_UP" :
							event = "MENU_VISIBLE";
							break;
						case "4_AXIS_FORWARD_UP" :
							if (menu_visible) {
								event = "SELECT_ACTIVE_MENU";
							}
							break;
						case "4_AXIS_BACKWARD_UP" :
							if (menu_visible) {
								event = "DESELECT_ACTIVE_MENU";
							}
							break;
						case "5_AXIS_FORWARD_UP" :
							if (menu_visible) {
								event = "BACK2PREVIOUSE_MENU";
							} else {
								event = "INCREMENT_PITCH";
							}
							break;
						case "5_AXIS_BACKWARD_UP" :
							if (menu_visible) {
								event = "GO2NEXT_MENU";
							} else {
								event = "DECREMENT_PITCH";
							}
							break;
					}
				}
				plugin.event_handler_act(event);
			},
			event_handler_act : function(event) {
				var params = event.split(" ");
				switch (params[0]) {
					case "FIRE" :
						m_plugin_host.send_command(OMNI_LASER_DOMAIN + "fire");
						break;
					case "FIRE_OFF" :
						m_plugin_host.send_command(OMNI_LASER_DOMAIN
							+ "fire_off");
						break;
					case "MOVE" :
						var cmd = OMNI_WHEEL_DOMAIN + "move";
						cmd += sprintf(" %.3f %.3f", MOVE_TIMEOUT, 50);
						m_plugin_host.send_command(cmd);
						break;
					case "STOP" :
						var cmd = OMNI_WHEEL_DOMAIN + "stop";
						cmd += sprintf(" %.3f %.3f", 0, 0);
						m_plugin_host.send_command(cmd);
						break;
					case "STEREO_ENABLED" :
						stereo_enabled = !stereo_enabled;
						m_plugin_host.send_command("set_stereo "
							+ (stereo_enabled ? "1" : "0"));
						break;
					case "MENU_VISIBLE" :
						menu_visible = !menu_visible;
						m_plugin_host.set_menu_visible(menu_visible);
						break;
					case "SELECT_ACTIVE_MENU" :
						m_plugin_host.send_command(SYSTEM_DOMAIN
							+ "select_active_menu");
						break;
					case "DESELECT_ACTIVE_MENU" :
						m_plugin_host.send_command(SYSTEM_DOMAIN
							+ "deselect_active_menu");
						break;
					case "BACK2PREVIOUSE_MENU" :
						m_plugin_host.send_command(SYSTEM_DOMAIN
							+ "back2previouse_menu");
						break;
					case "GO2NEXT_MENU" :
						m_plugin_host.send_command(SYSTEM_DOMAIN
							+ "go2next_menu");
						break;
					case "INCREMENT_YAW" :
						var quat = m_plugin_host.get_view_quaternion();
						quat = m_plugin_host.get_view_offset().multiply(quat);
						var cmd = OMNI_LASER_DOMAIN + "increment_yaw "
							+ (params[1] ? params[1] : step);
						m_plugin_host.send_command(cmd);
						break;
					case "DECREMENT_YAW" :
						var quat = m_plugin_host.get_view_quaternion();
						quat = m_plugin_host.get_view_offset().multiply(quat);
						var cmd = OMNI_LASER_DOMAIN + "increment_yaw -"
							+ (params[1] ? params[1] : step);
						m_plugin_host.send_command(cmd);
						break;
					case "INCREMENT_PITCH" :
						var quat = m_plugin_host.get_view_quaternion();
						quat = m_plugin_host.get_view_offset().multiply(quat);
						var cmd = OMNI_LASER_DOMAIN + "increment_pitch "
							+ (params[1] ? params[1] : step);
						m_plugin_host.send_command(cmd);
						break;
					case "DECREMENT_PITCH" :
						var quat = m_plugin_host.get_view_quaternion();
						quat = m_plugin_host.get_view_offset().multiply(quat);
						var cmd = OMNI_LASER_DOMAIN + "increment_pitch -"
							+ (params[1] ? params[1] : step);
						m_plugin_host.send_command(cmd);
						break;
				}
			}
		};
		return plugin;
	}
})();